import React, {Component, useState} from 'react';
import axios from 'axios';



function Login(props) {
    const userName = useFormInput('');
    const pwd = useFormInput('');
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    //handle button click of login form
    const handleLogin = () => {
        setError(null);
        setLoading(true);
        axios.post('http://localhost:7001/users/:userEmail/:userPassword', {userName: userName.value, pwd: pwd.value}).then(response => {
            setLoading(false);
            props.history.push('/api/projects')
        }).catch(error => {
            setLoading(false)
            if(error.response.status === 401) setError(error.response.data.message);
            else setError("Something went wrong.")
        })
        //props.history.push('/api/projects')
    }

    return(
        <div>
            Login<br /><br />
            <div>
                Username<br/>
                <input type="text" {...userName} autoComplete='new-password'/>
            </div>
            <div style={{marginTop: 10}}>
                Password<br/>
                <input type="password" {...pwd} autoComplete='new-password'/>
            </div>
            {error && <><small style={{ color: 'red' }}>{error}</small><br /></>}<br />
            <input type="button" value={loading ? 'Loading...' : 'Login'} onClick={handleLogin} disabled={loading} /><br />
        </div>
    );
}

const useFormInput = initialValue => {
    const [value, setValue] = useState(initialValue);

    const handleChange = e => {
        setValue(e.target.value);
    }
    return {
        value,
        onchange:handleChange
    }
}
